package nhathuy.com;

public class KieuDuLieu {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Các kiểu dữ liệu cơ sở và vùng giá trị của nó trong java: ");
		
		System.out.println("Kiểu boolean có Kích thước là: 1 bit(2 giá trị: True or false)\n");
		
		System.out.println("Kiểu char có kích thước là "+Character.BYTES + " byte \n");
		
		System.out.println("Kiểu byte có kích thước "+ Byte.BYTES + " byte(8 bit) có: ");
		System.out.println("Min của byte: " + Byte.MIN_VALUE);
		System.out.println("Max của byte: " + Byte.MAX_VALUE + "\n");
		
		System.out.println("Kiểu short có kích thước "+ Short.BYTES + " byte có: ");
		System.out.println("Min của short: " + Short.MIN_VALUE);
		System.out.println("Max của short: " + Short.MAX_VALUE + "\n");
		
		System.out.println("Kiểu int có kích thước "+ Integer.BYTES + " byte có: ");
		System.out.println("Min của int: " + Integer.MIN_VALUE);
		System.out.println("Max của int: " + Integer.MAX_VALUE + "\n");
		
		System.out.println("Kiểu long có kích thước "+ Long.BYTES + " byte có: ");
		System.out.println("Min của long: " + Long.MIN_VALUE);
		System.out.println("Max của long: " + Long.MAX_VALUE + "\n");
		
		System.out.println("Kiểu float có kích thước "+ Float.BYTES + " byte có: ");
		System.out.println("Min của float: " + Float.MIN_VALUE);
		System.out.println("Max của float: " + Float.MAX_VALUE + "\n");
		
		System.out.println("Kiểu double có kích thước "+ Double.BYTES + " byte có: ");
		System.out.println("Min của double: " + Double.MIN_VALUE);
		System.out.println("Max của double: " + Double.MAX_VALUE + "\n");
		
		
	}

}
